function GetKeyUp(className) {

    $(document).on('focus click', 'input', function (e) {
        var val = $(this).val();
        if (val == 0.00) {
            $(this).val('');
        }
    });

    $(document).on('keyup', '' + className + '', function (e) {
        e.preventDefault();
        var val = $(this).val();
        var re = /^([0-9,]+[\.]?[0-9]?[0-9]?|[0-9]+)$/g;
        var re1 = /^([0-9,]+[\.]?[0-9]?[0-9]?|[0-9]+)/g;
        if (this.value.length == 0) {
            $(this).val('');
        }
        if (re.exec(val)) {
            var beforeval = format_number(val);
            $(this).val(beforeval);
        } else {
            val = re1.exec(val);
            if (val) {
                $(this).val(val[0]);
            } else {
                $(this).val('');
            }
        }
        return true;
    });
}

function format_number(number, prefix, thousand_separator, decimal_separator){
    
        number = number.toString();
        var thousand_separator = thousand_separator || ',',
                decimal_separator = decimal_separator || '.',
                regex = new RegExp('[^' + decimal_separator + '\\d]', 'g'),
                number_string = number.replace(regex, '').toString(),
                split = number_string.split(decimal_separator),
                rest = split[0].length % 3,
                result = split[0].substr(0, rest),
                thousands = split[0].substr(rest).match(/\d{3}/g);
        if (thousands) {
            separator = rest ? thousand_separator : '';
            result += separator + thousands.join(thousand_separator);
        }
        result = split[1] != undefined ? result + decimal_separator + split[1] : result;
    
        return prefix == undefined ? result : (result ? prefix + result : '');   
        
}
function removeFormatterNumber(number) {

    if (parseFloat(number) != 'NaN' && $.trim(number) != '') {
        number = number.toString();
        let removed_format_number =  number.replace(/,/g, '');
        if(removed_format_number > 900719925474099){
            return BigInt(removed_format_number);
        }else{
            return parseFloat(number.replace(/,/g, ''));   
        }
    } else {
        return 0;
    }
}
function formateDate(date = '',format = ''){
  var formattedDate = date;
  if(format == 'yy-mm-dd'){
    // Input date in mm/dd/yyyy format
    var inputDate = date;
    // Split the input date into month, day, and year
    var parts = inputDate.split('/');
    var month = parseInt(parts[0], 10);
    var day = parseInt(parts[1], 10);
    var year = parseInt(parts[2], 10);

    // Create a new JavaScript Date object with the input date
    var dateObj = new Date(year, month - 1, day);

    // Use jQuery UI's date formatting function to convert it to yyyy-mm-dd format
    formattedDate = $.datepicker.formatDate('yy-mm-dd', dateObj);
  }else if (format == 'mm/dd/yyyy'){
    // Input date in yyyy-mm-dd format
    var inputDate = "2024-05-03";

    // Create a new Date object from the input date
    var dateObj = new Date(inputDate);

    // Use jQuery UI's formatDate function to format the date
    formattedDate = $.datepicker.formatDate('mm/dd/yy', dateObj);
  }

  return formattedDate;
}